document.addEventListener("DOMContentLoaded",function(){if(!document.querySelector(".transition-overlay")){const e=document.createElement("div");e.className="transition-overlay",document.body.appendChild(e)}document.querySelectorAll("a").forEach(function(e){e.hostname===window.location.hostname&&!e.hasAttribute("target")&&!e.getAttribute("href").startsWith("#")&&e.addEventListener("click",function(t){if(t.ctrlKey||t.shiftKey||t.metaKey||t.altKey)return;t.preventDefault();const n=e.href;document.body.classList.add("transition-active"),setTimeout(function(){window.location.href=n},300)})})}),window.addEventListener("pageshow",function(){document.body.classList.contains("transition-active")&&document.body.classList.remove("transition-active")})